<div id="widget-loved" class="widget widget-table action-table">							<div class="widget-header">		<i class="icon-heart"></i>		<h3>Loved songs</h3>	</div> <!-- /widget-header -->		<div class="widget-content">				<table class="table table-striped table-bordered">				<!--			<thead>				<tr>					<th width="80%"><i class="icon-music"></i> Song</th>					<th width="20%"><i class="icon-user"></i> User</th>				</tr>			</thead>			-->			<tbody id="widget-loved-container">							</tbody>		</table>							</div> <!-- /widget-content --></div><script id="widget-loved-template" type="text/x-handlebars-template">	{{#each loves}}				<tr>			<td width="80%">				<a href="/song/play/{{song.id}}" class="tag-music" title="Song: {{song.title}}">{{song.title}}</a>			</td>			<td>				<i class="icon-user"></i> <a href="/user/show/{{user.id}}" class="tag-socialx" title="User: {{user.name}}">{{user.name}}</a><br />				<i class="icon-align-justify"></i> <a href="/playlist/show/{{playlist.id}}" class="tag-collabx" title="Playlist: {{playlist.name}}">{{playlist.name}}</a>			</td>		</tr>			{{/each}}</script><script>		var widget_loved_id = setInterval(function() { reload_widget_loved(); },  <?php echo Kohana::$config->load('settings.refresh.loved'); ?> );		$(document).ready(function() {		reload_widget_loved();	});			function reload_widget_loved() {		$.getJSON('api/widget_loved', {'limit': <?php echo $limit; ?>}, function(data) {			var results = $('#widget-loved-container');			var source = $("#widget-loved-template").html();			var template = Handlebars.compile(source);			results.html(template(data));			});	}		</script>